import axios from "axios";
import {
    GET_USERS,
    GET_USER,
    UPDATE_USERS,
    USERS_LOADING
} from "./types";

export const getUsers = () => dispatch => {
    dispatch(setUsersLoading());
    axios
    .get("/api/users/users")
    .then(res =>
        dispatch({
            type: GET_USERS,
            payload: res.data
        })
    )
    .catch(err =>
        dispatch({
            type: GET_USERS,
            payload: null
        })
    );
};

export const setUsersLoading = () => {
    return {
        type: USERS_LOADING
    };
};
