import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import classnames from "classnames";

class AssetForm extends Component {
  constructor() {
    super();
    this.state = {
      desc: "",
      unidad: "",
      cantidad: "",
      categoria: "",
      ubicacion:"",
      proveedor:"",
      frec_mant:"",
      ruta_imag:"",
      ruta_manual:"",
      garantia:"",
      estado:"Creado",
      errors: {}
    };
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

onSubmit = e => {
    e.preventDefault();

const newAsset = {
    desc: this.state.desc,
    unidad: this.state.unidad,
    cantidad: this.state.cantidad,
    categoria: this.state.categoria,
    ubicacion: this.state.ubicacion,
    proveedor: this.state.proveedor,
    frec_mant: this.state.frec_mant,
    ruta_imag: this.state.ruta_imag,
    ruta_manual: this.state.ruta_manual,
    garantia: this.state.garantia,
    estado: this.state.estado
    };
    this.props.addAsset(newAsset, this.props.history); 
};
  
render() {
    const { errors } = this.state;
return (
      <div className="container">
        <div className="row">
          <div className="col s8 offset-s2">
            <Link to="/" className="btn-flat waves-effect">
              <i className="material-icons left">keyboard_backspace</i> Back to
              home
            </Link>
            <div className="col s12" style={{ paddingLeft: "11.250px" }}>
              <h4>
                <b>Nuevo activo</b> :
              </h4>
            </div>
            <form noValidate onSubmit={this.onSubmit}>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.desc}
                  error={errors.desc}
                  id="desc"
                  type="text"
                  className={classnames("", {
                    invalid: errors.desc
                  })}
                />
                <label htmlFor="desc">Descripción</label>
                <span className="red-text">{errors.desc}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.unidad}
                  error={errors.unidad}
                  id="unidad"
                  type="text"
                  className={classnames("", {
                    invalid: errors.unidad
                  })}
                />
                <label htmlFor="unidad">Unidad</label>
                <span className="red-text">{errors.unidad}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.cantidad}
                  error={errors.cantidad}
                  id="cantidad"
                  type="text"
                  className={classnames("", {
                    invalid: errors.cantidad
                  })}
                />
                <label htmlFor="role">Cantidad</label>
                <span className="red-text">{errors.cantidad}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.categoria}
                  error={errors.categoria}
                  id="categoria"
                  type="text"
                  className={classnames("", {
                    invalid: errors.categoria
                  })}
                />
                <label htmlFor="categoria">Categoría</label>
                <span className="red-text">{errors.categoria}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.ubicacion}
                  error={errors.ubicacion}
                  id="ubicacion"
                  type="text"
                  className={classnames("", {
                    invalid: errors.ubicacion
                  })}
                />
                <label htmlFor="ubicacion">Ubicación</label>
                <span className="red-text">{errors.ubicacion}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.ubicacion}
                  error={errors.ubicacion}
                  id="ubicacion"
                  type="text"
                  className={classnames("", {
                    invalid: errors.ubicacion
                  })}
                />
                <label htmlFor="ubicacion">Ubicación</label>
                <span className="red-text">{errors.ubicacion}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.proveedor}
                  error={errors.proveedor}
                  id="proveedor"
                  type="text"
                  className={classnames("", {
                    invalid: errors.proveedor
                  })}
                />
                <label htmlFor="proveedor">Proveedor</label>
                <span className="red-text">{errors.proveedor}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.frec_mant}
                  error={errors.frec_mant}
                  id="frec_mant"
                  type="text"
                  className={classnames("", {
                    invalid: errors.frec_mant
                  })}
                />
                <label htmlFor="frec_mant">Frecuencia mantenimiento</label>
                <span className="red-text">{errors.frec_mant}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.ruta_imag}
                  error={errors.ruta_imag}
                  id="ruta_imag"
                  type="text"
                  className={classnames("", {
                    invalid: errors.ruta_imag
                  })}
                />
                <label htmlFor="ruta_imag">Ruta imagen</label>
                <span className="red-text">{errors.ruta_imag}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.ruta_manual}
