import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

import { getAssets, updateAsset, addAsset } from "../../actions/assetActions";

import Assets from "./Assets";

class DashAssets extends Component {
    componentDidMount() {
        this.props.getAssets();
        console.log()
    }

    render() {
        const { assets, assetsLoading } = this.props.assets;

        console.log(this.props)

        let dashboardContent;

        const res = assets
        
        console.log(res)

        if (res === null || assetsLoading) {
            dashboardContent = <p className="center-align">Loading...</p>;
          } else if (res.length> 0) {
            dashboardContent = <Assets assets={assets}/>;
          } else {
            dashboardContent = <div> No hay activos </div>;
          }

        return (
            <div className="container">
                {dashboardContent}
                <Link
                  to="/newAsset"
                  style={{
                    width: "140px",
                    borderRadius: "3px",
                    letterSpacing: "1.5px"
                  }}
                  className="btn btn-large btn-flat waves-effect white black-text"
                >
                  Nuevo activo
                </Link>
            </div>
        )
    }
}

DashAssets.propTypes = {
    getAssets: PropTypes.func.isRequired,
    assets: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    assets: state.assets
});

export default connect(
    mapStateToProps,
    {getAssets , updateAsset, addAsset}
) (DashAssets);
