import axios from "axios";
import {
    GET_ERRORS,
    GET_ASSETS,
    ADD_ASSET,
    UPDATE_ASSET,
    ASSETS_LOADING
} from "./types";

export const getAssets = () => dispatch => {
    dispatch(setAssetsLoading());
    axios
    .get("/api/users/assets")
    .then(res =>
        dispatch({
            type: GET_ASSETS,
            payload: res.data
        })
    )
    .catch(err =>
        dispatch({
            type: GET_ASSETS,
            payload: null
        })
    );
};

export const addAsset = (assetData) => dispatch => {
    axios
    .post("/api/assets/newAsset", assetData)
    .then(res => history.push("../assets"))
    .catch(err =>
        dispatch({
          type: GET_ERRORS,
          payload: err.response.data
        })
    );
}

export const updateAsset = (assetData) => dispatch => {
    
}

export const setAssetsLoading = () => {
    return {
        type: ASSETS_LOADING
    };
};
