import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addRoutine } from "../../actions/routineActions";
import classnames from "classnames";

class RoutineForm extends Component {
  constructor() {
    super();
    this.state = {
      fecha: "",
      ejecutor: "",
      supervisor: "",
      espacio_vip: "",
      espacio_rutina: "",
      espacio_local: "",
      espacio_banio: "",
      espacio_parq: "",
      espacio_fach: "",
      espacio_pant: "",
      espacio_rci: "",
      observaciones: "",
      recinto: "",
      errors: {}
    };
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

onSubmit = e => {
    e.preventDefault();

const newRoutine = {
    fecha: this.state.fecha,
    ejecutor: this.state.ejecutor,
    supervisor: this.state.supervisor,
    espacio_vip: this.state.espacio_vip,
    espacio_rutina: this.state.espacio_rutina,
    espacio_local: this.state.espacio_local,
    espacio_banio: this.state.espacio_banio,
    espacio_parq: this.state.espacio_parq,
    espacio_fach: this.state.espacio_fach,
    espacio_pant: this.state.espacio_pant,
    espacio_rci: this.state.espacio_rci,
    observaciones: this.state.observaciones,
    recinto: this.state.recinto,
    errors: {}
    };
    this.props.addRoutine(newRoutine, this.props.history); 
};
  
render() {
    const { errors } = this.state;
return (
      <div className="container">
        <div className="row">
          <div className="col s8 offset-s2">
            <Link to="/routines" className="btn-flat waves-effect">
              <i className="material-icons left">keyboard_backspace</i> Back to
              routines
            </Link>
            <div className="col s12" style={{ paddingLeft: "11.250px" }}>
              <h4>
                <b>Nueva Rutina</b> :
              </h4>
            </div>
            <form noValidate onSubmit={this.onSubmit}>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.activo}
                  error={errors.activo}
                  id="activo"
                  type="text"
                  className={classnames("", {
                    invalid: errors.activo
                  })}
                />
                <label htmlFor="activo">Activo</label>
                <span className="red-text">{errors.activo}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.frecuencia}
                  error={errors.frecuencia}
                  id="frecuencia"
                  type="text"
                  className={classnames("", {
                    invalid: errors.frecuencia
                  })}
                />
                <label htmlFor="frecuencia">Frecuencia </label>
                <span className="red-text">{errors.frecuencia}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.responsable}
                  error={errors.responsable}
                  id="responsable"
                  type="text"
                  className={classnames("", {
                    invalid: errors.responsable
                  })}
                />
                <label htmlFor="responsable">Responsable</label>
                <span className="red-text">{errors.responsable}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.email_reporte}
                  error={errors.email_reporte}
                  id="email_reporte"
                  type="email"
                  className={classnames("", {
                    invalid: errors.email_reporte
                  })}
                />
                <label htmlFor="email_reporte">Email</label>
                <span className="red-text">{errors.email_reporte}</span>
              </div>
              <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                <button
                  style={{
                    width: "150px",
                    borderRadius: "3px",
                    letterSpacing: "1.5px",
                    marginTop: "1rem"
                  }}
                  type="submit"
                  className="btn btn-large waves-effect waves-light hoverable blue accent-3"
                >
                  Crear
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

RoutineForm.propTypes = {
    addRoutine: PropTypes.func.isRequired,
    errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    errors: state.errors
});

export default connect(
    mapStateToProps,
    { addRoutine }
)(RoutineForm);
