import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { changePassword } from "../../actions/authActions";
import classnames from "classnames";

class Profile extends Component {

    constructor (){
        super();
        this.state = {
            id: "",
            oldPass: "",
            newPass: "",
            errors: {}
        };
    }

    componentWillReceiveProps(nextProps) {
        
        if (nextProps.errors) {
            this.setState({
              errors: nextProps.errors
            });
        }
    }

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };

    onUpdateClick = id => {
        const profileData = {
            id: this.props.auth.user.id,
            oldPass: this.state.oldPass,
            newPass: this.state.newPass
            };
        console.log(profileData)
        this.props.changePassword(profileData);
    }

    render() {
        const profile = this.props.auth.user
        const { errors } = this.state;
        
        return (
            <div className="container">
                <Link to="/Dashboard" className="btn-flat waves-effect">
                <i className="material-icons left">keyboard_backspace</i> Regresar
                </Link>
                <div className="row"> 
                    <div className="col s12 center-align">
                        <h5>
                            <b>Perfil</b>
                        </h5>
                        <br/>
                    </div>
                </div>
                <div className="row">
                    <div className="col s6">
                        Nombre: <input type="text" readOnly value={profile.name} />
                    </div>
                </div>
                <div className="row">
                    <div className="col s6">
                        Documento: <input type="text" readOnly value={profile.documento} />
                    </div>
                </div>
                <div className="row">
                    <div className="col s6">
                        Email: <input type="text" readOnly value={profile.email} />
                    </div>
                </div>
                <div className="row">
                    <br/>
                    <h5><b>Cambiar Contrase単a</b></h5>
                    <br/>
                    <div className="input-field col s6">
                        <input
                            onChange={this.onChange}
                            value={this.state.oldPass}
                            error={errors.oldPass}
                            id="oldPass"
                            type="password"
                            className={classnames("", {
                                invalid: errors.oldPass || errors.passwodIncorrect
                            })}
                        />
                        <label htmlFor="password">Contrase単a:</label>
                         <span className="red-text">{errors.oldPass}</span>
                    </div>
                </div>
                <div className="row">
                    <div className="input-field col s6">
                        <input
                            onChange={this.onChange}
                            value={this.state.newPass}
                            error={errors.newPass}
                            id="newPass"
                            type="password"
                            className={classnames("", {
                                invalid: errors.newPass || errors.passwodIncorrect
                            })}
                        />
                        <label htmlFor="password">Nueva contrase単a:</label>
                        <span className="red-text">{errors.newPass}</span>
                    </div>
                </div>
                <button
                    style={{ marginRight: "1rem" }}
                    onClick={this.onUpdateClick.bind(this, profile._id)}
                    className="btn btn-medium waves-effect waves-light hoverable blue accent-3">
                    <i className="material-icons">update</i> Cambiar contrase単a
                </button>
                <br/>
                <div className="row">
                    <div className="col">

                    </div>
                </div>
            </div>
        )
    }
}

Profile.propTypes = {
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
});

export default connect(
    mapStateToProps,
    { changePassword }
)(Profile)
