import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addTask } from "../../actions/taskActions";
import classnames from "classnames";
import {SingleDatePicker} from "react-dates";
import 'react-dates/initialize';
import 'react-dates/lib/css/_datepicker.css';
import moment from "moment";

class TaskForm extends Component {
  constructor() {
    super();
    this.state = {
      activo: "",
      start_date: moment(),
      end_date: moment(),
      tipo_mant: "",
      desc:"",
      ruta_imag:"",
      tipo_responsable:"",
      responsable:"",
      estado:"Creada",
      focused: false,
      mousef: false,
      errors: {}
    };
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

onSubmit = e => {
    e.preventDefault();

const newTask = {
    activo: this.state.activo,
    start_date: this.state.start_date,
    end_date: this.state.end_date,
    tipo_mant: this.state.tipo_mant,
    desc: this.state.desc,
    ruta_imag: this.state.ruta_imag,
    tipo_responsable: this.state.tipo_responsable,
    responsable: this.state.responsable,
    estado: this.state.responsable
    };
    this.props.addTask(newTask, this.props.history); 
};
  
render() {
    const { errors } = this.state;
return (
      <div className="container">
        <div className="row">
          <div className="col s8 offset-s2">
            <Link to="/tasks" className="btn-flat waves-effect">
              <i className="material-icons left">keyboard_backspace</i> Back to
              tasks
            </Link>
            <div className="col s12" style={{ paddingLeft: "11.250px" }}>
              <h4>
                <b>Nueva Actividad</b> :
              </h4>
            </div>
            <form noValidate onSubmit={this.onSubmit}>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.activo}
                  error={errors.activo}
                  id="activo"
                  type="text"
                  className={classnames("", {
                    invalid: errors.activo
                  })}
                />
                <label htmlFor="activo">Activo</label>
                <span className="red-text">{errors.activo}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.start_date}
                  error={errors.start_date}
                  id="start_date"
                  type="date"
                  className={classnames("", {
                    invalid: errors.start_date
                  })}
                />
                <label htmlFor="start_date">Fecha de inicio</label>
                <span className="red-text">{errors.start_date}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.end_date}
                  error={errors.end_date}
                  id="end_date"
                  type="date"
                  className={classnames("", {
                    invalid: errors.end_date
                  })}
                />
                <label htmlFor="end_date">Fecha finalización</label>
                <span className="red-text">{errors.end_date}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.tipo_mant}
                  error={errors.tipo_mant}
                  id="tipo_mant"
                  type="text"
                  className={classnames("", {
                    invalid: errors.tipo_mant
                  })}
                />
                <label htmlFor="tipo_mant">Tipo de mantenimiento</label>
                <span className="red-text">{errors.tipo_mant}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.desc}
                  error={errors.desc}
                  id="desc"
                  type="text"
                  className={classnames("", {
                    invalid: errors.desc
                  })}
                />
                <label htmlFor="desc">Descripción</label>
                <span className="red-text">{errors.desc}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.ruta_imag}
                  error={errors.ruta_imag}
                  id="ruta_imag"
                  type="text"
                  className={classnames("", {
                    invalid: errors.ruta_imag
                  })}
                />
                <label htmlFor="ruta_imag">Imagen</label>
                <span className="red-text">{errors.ruta_imag}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.tipo_responsable}
                  error={errors.tipo_responsable}
                  id="tipo_responsable"
                  type="text"
                  className={classnames("", {
                    invalid: errors.tipo_responsable
                  })}
                />
                <label htmlFor="tipo_responsable">Tipo de responsable</label>
                <span className="red-text">{errors.tipo_responsable}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.responsable}
                  error={errors.responsable}
                  id="responsable"
                  type="text"
                  className={classnames("", {
                    invalid: errors.responsable
                  })}
                />
                <label htmlFor="responsable">Responsable</label>
                <span className="red-text">{errors.responsable}</span>
              </div>
              <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                <button
                  style={{
                    width: "150px",
                    borderRadius: "3px",
                    letterSpacing: "1.5px",
                    marginTop: "1rem"
                  }}
                  type="submit"
                  className="btn btn-large waves-effect waves-light hoverable blue accent-3"
                >
                  Crear
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

TaskForm.propTypes = {
    addTask: PropTypes.func.isRequired,
    errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    errors: state.errors
});

export default connect(
    mapStateToProps,
    { addTask }
)(TaskForm);
