import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";

class Assets extends Component {

    onUpdateClick = id => {
        const { users } = this.props;
        const userData = {
        id: id,
        users: users
        };
        this.props.updateUser(userData);
    }

    render() {
        const res = this.props.assets.assets;
        const assets = res.data;

        let assetItems = assets.map(asset => (
            <li key={asset._id} style={{ marginTop: "1rem" }}>
                <button
                    style={{ marginRight: "1rem" }}
                    onClick={this.onUpdateClick.bind(this, asset._id)}
                    className="btn btn-small btn-floating waves-effect waves-light hoverable red accent-3"
                >
                    <i className="material-icons">update</i>
                </button>
                <b>{asset.desc} - {asset.cantidad} - {asset.categoria} - {asset.ubicacion} - {asset.frec_mant} - {asset.estado}</b>
            </li>
        ));

        return (
            <div className="row">
                <div className="col s12">
                    <h5>
                        <b>Activos</b>
                    </h5>
                    <p className="grey-text text-darken-1">
                        Agregar o modificar activos
                    </p>
                    <ul>{assetItems}</ul>
                </div>
            </div>
        );
    }
}

Assets.propTypes = {
    assets: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
    assets: state.assets
  });

export default connect(
    mapStateToProps,
    {  }
)(Assets);
