import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";

import { getUsers, deleteUser } from "../../actions/userActions";

import Users from "./Users";

// <Users users={users} />;

class DashUsers extends Component {
    componentDidMount() {
        this.props.getUsers();
      }

    render() {
        const { users, usersLoading } = this.props.users;

        console.log(users)
        console.log(usersLoading)

        let dashboardContent;

        const res = Object.values(users)

        if (res === null || usersLoading) {
            dashboardContent = <p className="center-align">Loading...</p>;
          } else if (res.length> 0) {
            dashboardContent = <Users users={users}/>;
          } else {
            dashboardContent = <div> No hay usuarios </div>;
          }

        return (
            <div className="container">
                {dashboardContent}
            </div>
        )
    }
}

DashUsers.propTypes = {
    getUsers: PropTypes.func.isRequired,
    users: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    users: state.users
});

export  default connect(
    mapStateToProps,
    {getUsers}
) (DashUsers);
