import axios from "axios";
import {
    GET_ASSETS,
    ADD_ASSET,
    UPDATE_ASSET,
    ASSETS_LOADING
} from "./types";

export const getAssets = () => dispatch => {
    dispatch(setUsersLoading());
    axios
    .get("/api/users/assets")
    .then(res =>
        dispatch({
            type: GET_ASSETS,
            payload: res.data
        })
    )
    .catch(err =>
        dispatch({
            type: GET_ASSETS,
            payload: null
        })
    );
};

