import {
    SET_CURRENT_USER,
    USER_LOADING,
    GET_PROFILE
  } from "../actions/types";
  const isEmpty = require("is-empty");
  const initialState = {
    isAuthenticated: false,
    user: {},
    profile: null,
    loading: false
  };
  export default function(state = initialState, action) {
    switch (action.type) {
      case SET_CURRENT_USER:
        return {
          ...state,
          isAuthenticated: !isEmpty(action.payload),
          user: action.payload
        };
      case GET_PROFILE:
        return {
          ...state,
          profile: action.payload,
          USER_LOADING: false
        }
      case USER_LOADING:
        return {
          ...state,
          loading: true
        };
      default:
        return state;
    }
  }
