import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { changePassword } from "../../actions/authActions";
import classnames from "classnames";

class Profile extends Component {

    constructor (){
        super();
        this.state = {
            id: "",
            oldPass: "",
            newPass: "",
            errors: {}
        };
    }

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
      };

    onUpdateClick = id => {
        const profileData = {
            id: id,
            oldPass: this.state.oldPass,
            newPass: this.state.newPass
            };
        this.props.changePassword(profileData);
    }

    render() {
        const profile = this.props.auth.user
        const { errors } = this.state;
        
        console.log(profile)
        return (
            <div className="container">
                <Link to="/Dashboard" className="btn-flat waves-effect">
                <i className="material-icons left">keyboard_backspace</i> Regresar
                </Link>
                <div className="row"> 

                <div className="col s12 center-align">
                    <h4>
                        <b>Perfil</b>
                    </h4>
                    <br/>
                </div>
                </div>
                <div className="row">
                    <div className="col s3">
                        Nombre: <input type="text" value={profile.name} />
                    </div>
                </div>
                <div className="row">
                    <div className="col s3">
                        Email: <input type="text" value={profile.email} />
                    </div>
                </div>
                <div className="row">
                    <br/>
                    <h4>Cambiar Contraseña</h4>
                    <br/>
                    <div className="col s3">
                        Constraseña: 
                        <input
                            onChange={this.onChange}
                            value={this.state.oldPass}
                            error={errors.oldPass}
                            id="oldPass"
                            type="password"
                            className={classnames("", {
                                invalid: errors.oldPass || errors.passwodIncorrect
                            })}
                        />
                    </div>
                </div>
                <div className="row">
                    <div className="col s3">
                        Nueva constraseña:
                        <input
                            onChange={this.onChange}
                            value={this.state.newPass}
                            error={errors.newPass}
                            id="newPass"
                            type="password"
                            className={classnames("", {
                                invalid: errors.newPass || errors.passwodIncorrect
                            })}
                        />
                    </div>
                </div>
                <button
                    style={{ marginRight: "1rem" }}
                    onClick={this.onUpdateClick.bind(this, profile._id)}
                    className="btn btn-medium waves-effect waves-light hoverable blue accent-3">
                    <i className="material-icons">update</i> Actualizar contraseña
                </button>
            </div>
        )
    }
}

Profile.propTypes = {
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
});

export default connect(
    mapStateToProps,
    { changePassword }
)(Profile)
