import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";

class Tasks extends Component {

    onUpdateClick = id => {
        const { users } = this.props;
        const userData = {
        id: id,
        users: users
        };
        this.props.updateUser(userData);
    }

    render() {
        const res = this.props.tasks.tasks;
        const tasks = res.data;

        let taskItems = tasks.map(task => (
            <li key={task._id} style={{ marginTop: "1rem" }}>
                <button
                    style={{ marginRight: "1rem" }}
                    onClick={this.onUpdateClick.bind(this, task._id)}
                    className="btn btn-small btn-floating waves-effect waves-light hoverable red accent-3"
                >
                    <i className="material-icons">update</i>
                </button>
                <b>{task.desc} - {task.cantidad} - {task.categoria} - {task.ubicacion} - {task.frec_mant} - {task.estado}</b>
            </li>
        ));

        return (
            <div className="row">
                <div className="col s12">
                    <h5>
                        <b>Activos</b>
                    </h5>
                    <p className="grey-text text-darken-1">
                        Agregar o modificar activos
                    </p>
                    <ul>{taskItems}</ul>
                </div>
            </div>
        );
    }
}

Tasks.propTypes = {
    tasks: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
    tasks: state.tasks
  });

export default connect(
    mapStateToProps,
    {  }
)(Tasks);
