import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addTask } from "../../actions/taskActions";
import classnames from "classnames";
import {SingleDatePicker} from "react-dates";

class TaskForm extends Component {
  constructor() {
    super();
    this.state = {
      activo: "",
      start_date: moment(),
      end_date: moment(),
      tipo_mant: "",
      desc:"",
      ruta_imag:"",
      tipo_responsable:"",
      responsable:"",
      estado:"Creada",
      focused: false,
      errors: {}
    };
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

onSubmit = e => {
    e.preventDefault();

const newTask = {
    activo: this.state.activo,
    start_date: this.state.start_date,
    end_date: this.state.end_date,
    tipo_mant: this.state.tipo_mant,
    desc: this.state.desc,
    ruta_imag: this.state.ruta_imag,
    tipo_responsable: this.state.tipo_responsable,
    responsable: this.state.responsable,
    estado: this.state.responsable
    };
    this.props.addTask(newTask, this.props.history); 
};
  
render() {
    const { errors } = this.state;
return (
      <div className="container">
        <div className="row">
          <div className="col s8 offset-s2">
            <Link to="/tasks" className="btn-flat waves-effect">
              <i className="material-icons left">keyboard_backspace</i> Back to
              tasks
            </Link>
            <div className="col s12" style={{ paddingLeft: "11.250px" }}>
              <h4>
                <b>Nueva Actividad</b> :
              </h4>
            </div>
            <form noValidate onSubmit={this.onSubmit}>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.activo}
                  error={errors.activo}
                  id="activo"
                  type="text"
                  className={classnames("", {
                    invalid: errors.activo
                  })}
                />
                <label htmlFor="activo">Activo</label>
                <span className="red-text">{errors.activo}</span>
              </div>
              <div className="input-field col s12">
                <SingleDatePicker
                  date={this.state.start_date} // momentPropTypes.momentObj or null
                  onDateChange={this.onChange} // PropTypes.func.isRequired
                  focused={this.state.focused} // PropTypes.bool
                  onFocusChange={({ focused }) => this.setState({ focused })} // PropTypes.func.isRequired
                  id="start_date" // PropTypes.string.isRequired,
                />
                <label htmlFor="start_date">Fecha inicio</label>
                <span className="red-text">{errors.start_date}</span>
              </div>
              <div className="input-field col s12">
                <SingleDatePicker
                  date={this.state.end_date} // momentPropTypes.momentObj or null
                  onDateChange={this.onChange} // PropTypes.func.isRequired
                  focused={this.state.focused} // PropTypes.bool
                  onFocusChange={({ focused }) => this.setState({ focused })} // PropTypes.func.isRequired
                  id="end_date" // PropTypes.string.isRequired,
                  
                />
                <label htmlFor="end_date">Fecha finalización</label>
                <span className="red-text">{errors.end_date}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.ubicacion}
                  error={errors.ubicacion}
                  id="ubicacion"
                  type="text"
                  className={classnames("", {
                    invalid: errors.ubicacion
                  })}
                />
                <label htmlFor="ubicacion">Ubicación</label>
                <span className="red-text">{errors.ubicacion}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.proveedor}
                  error={errors.proveedor}
                  id="proveedor"
                  type="text"
                  className={classnames("", {
                    invalid: errors.proveedor
                  })}
                />
                <label htmlFor="proveedor">Proveedor</label>
                <span className="red-text">{errors.proveedor}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.frec_mant}
                  error={errors.frec_mant}
                  id="frec_mant"
                  type="text"
                  className={classnames("", {
                    invalid: errors.frec_mant
                  })}
                />
                <label htmlFor="frec_mant">Frecuencia mantenimiento</label>
                <span className="red-text">{errors.frec_mant}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.ruta_imag}
                  error={errors.ruta_imag}
                  id="ruta_imag"
                  type="text"
                  className={classnames("", {
                    invalid: errors.ruta_imag
                  })}
                />
                <label htmlFor="ruta_imag">Ruta imagen</label>
                <span className="red-text">{errors.ruta_imag}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.ruta_manual}
                  error={errors.ruta_manual}
                  id="ruta_manual"
                  type="text"
                  className={classnames("", {
                    invalid: errors.ruta_manual
                  })}
                />
                <label htmlFor="ruta_manual">Ruta manual</label>
                <span className="red-text">{errors.ruta_manual}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.garantia}
                  error={errors.garantia}
                  id="garantia"
                  type="text"
                  className={classnames("", {
                    invalid: errors.garantia
                  })}
                />
                <label htmlFor="garantia">Garantía</label>
                <span className="red-text">{errors.garantia}</span>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.estado}
                  error={errors.estado}
                  id="estado"
                  type="text"
                  className={classnames("", {
                    invalid: errors.estado
                  })}
                />
                <label htmlFor="estado">Estado</label>
                <span className="red-text">{errors.estado}</span>
              </div>
              <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                <button
                  style={{
                    width: "150px",
                    borderRadius: "3px",
                    letterSpacing: "1.5px",
                    marginTop: "1rem"
                  }}
                  type="submit"
                  className="btn btn-large waves-effect waves-light hoverable blue accent-3"
                >
                  Crear
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

TaskForm.propTypes = {
    addTask: PropTypes.func.isRequired,
    errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    errors: state.errors
});

export default connect(
    mapStateToProps,
    { addTask }
)(TaskForm);
